const Platillos= ()=>{
    const platillos = [
        {
          nombre: 'Pasta Carbonara',
          imagenes: [
            'https://www.elmueble.com/medio/2023/02/08/25-recetas-de-tupper-para-preparar-el-dia-antes_afc5e2b6_230208170301_900x900.jpg',
            'https://s3.abcstatics.com/media/gurmesevilla/2012/01/comida-rapida-casera.jpg',
            'https://www.clara.es/medio/2021/12/16/que-comer-hoy-ideas_21beeb02_1200x630.jpg',
          ],
        },
        {
          nombre: 'Sushi Variado',
          imagenes: [
            'https://via.placeholder.com/300',
            'https://via.placeholder.com/300',
            'https://via.placeholder.com/300',
          ],
        },
        // Agregar m치s platillos con sus respectivas im치genes
      ];
    
      return (
        <div>
          <h2>Platillos</h2>
          <p>Descubre nuestros deliciosos platillos</p>
    
          <div>
            {platillos.map((platillo, index) => (
              <div key={index}>
                <h3>{platillo.nombre}</h3>
                <div style={{ display: 'flex' }}>
                  {platillo.imagenes.map((imagen, idx) => (
                    <img
                      key={idx}
                      src={imagen}
                      alt={`${platillo.nombre} - Imagen ${idx + 1}`}
                      style={{ width: '200px', height: '150px', marginRight: '10px' }}
                    />
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>
      );
    };
export default Platillos
